// The input from the previous node is an array of items. We'll work with the first item.
const item = items[0];

// --- IMPORTANT ---
// The 'Extract from File' node will place the file's content into a property.
// You may need to adjust 'fileContent' to match the actual property name from that node.
const jsonString = item.json.data;

if (!jsonString) {
  throw new Error("The property 'fileContent' was not found in the input data. Please check the output of the 'Extract from File' node and update the property name in this code.");
}

// Parse the large JSON string into an array of individual workflow objects.
const workflows = JSON.parse(jsonString);

// List of settings properties that are not supported by the n8n API
const unsupportedSettingsProperties = [
  'executionOrder',
  'callerPolicy', 
  'errorWorkflow',
  'timeSavedPerExecution',
  'executionTimeout',
  'maxExecutionTimeout'
];

// Return each workflow as a separate item.
// For each item, convert the workflow object back into a string
// and place it in a property called 'data'.
return workflows.map(workflow => {
  // Extract workflow name and ID for easier reference
  const workflowName = workflow.name || 'Unnamed Workflow';
  const workflowId = workflow.id || 'No ID';

  // Create a cleaned copy of the workflow
  const cleanedWorkflow = { ...workflow };

  // Clean up the settings object by removing unsupported properties
  if (cleanedWorkflow.settings) {
    const cleanedSettings = { ...cleanedWorkflow.settings };

    // Remove unsupported properties
    unsupportedSettingsProperties.forEach(prop => {
      delete cleanedSettings[prop];
    });

    cleanedWorkflow.settings = cleanedSettings;
  }

  // Remove properties that shouldn't be included when creating new workflows
  delete cleanedWorkflow.id;           // Let n8n assign a new ID
  delete cleanedWorkflow.createdAt;    // Will be set by n8n
  delete cleanedWorkflow.updatedAt;    // Will be set by n8n
  delete cleanedWorkflow.versionId;    // Will be set by n8n
  delete cleanedWorkflow.shared;       // Will be handled by n8n
  delete cleanedWorkflow.triggerCount; // Will be reset by n8n

  // Clean up staticData - remove if empty or clean node-specific data
  if (cleanedWorkflow.staticData) {
    const cleanedStaticData = { ...cleanedWorkflow.staticData };
    // Remove any node-specific data that might reference old node IDs
    Object.keys(cleanedStaticData).forEach(key => {
      if (key.startsWith('node:')) {
        delete cleanedStaticData[key];
      }
    });
    // Remove staticData entirely if it's empty after cleaning
    if (Object.keys(cleanedStaticData).length === 0) {
      delete cleanedWorkflow.staticData;
    } else {
      cleanedWorkflow.staticData = cleanedStaticData;
    }
  }

  // Keep pinData as it contains important test/development data
  // Only remove pinData if it's undefined, but preserve empty objects

  // Clean up meta object - remove or clean problematic properties
  if (cleanedWorkflow.meta) {
    const cleanedMeta = { ...cleanedWorkflow.meta };
    // Remove properties that might cause issues with new workflow creation
    delete cleanedMeta.templateCredsSetupCompleted;
    delete cleanedMeta.instanceId;

    // Remove meta entirely if it's empty after cleaning
    if (Object.keys(cleanedMeta).length === 0) {
      delete cleanedWorkflow.meta;
    } else {
      cleanedWorkflow.meta = cleanedMeta;
    }
  }

  // Clean webhook IDs from nodes that might conflict
  if (cleanedWorkflow.nodes) {
    cleanedWorkflow.nodes = cleanedWorkflow.nodes.map(node => {
      const cleanedNode = { ...node };

      // Remove webhookId property that might reference old webhook instances
      if (cleanedNode.webhookId) {
        delete cleanedNode.webhookId;
      }

      return cleanedNode;
    });
  }

  return {
    json: {
      data: JSON.stringify(cleanedWorkflow),
      workflowName: workflowName,
      originalId: workflowId,
      // Additional metadata for debugging
      hasSettings: !!workflow.settings,
      nodeCount: workflow.nodes ? workflow.nodes.length : 0
    }
  };
});
