const htmlContent = $input.first().json.content.rendered

const titles = [];
const paragraphs = [];

const titleMatches = htmlContent.match(/<h[1-6][^>]*>(.*?)<\/h[1-6]>/g);
if (titleMatches) {
    for (let match of titleMatches) {
        titles.push(match.replace(/<[^>]+>/g, ''));
    }
}

const paragraphMatches = htmlContent.match(/<p[^>]*>(.*?)<\/p>/g);
if (paragraphMatches) {
    for (let match of paragraphMatches) {
        paragraphs.push(match.replace(/<[^>]+>/g, ''));
    }
}

const content = "T√≠tulos:\n" + titles.join("\n") + "\n\nP√°rrafos:\n" + paragraphs.join(" ");

const maxCharacters = 10000;
let finalContent = content;
if (finalContent.length > maxCharacters) {
    finalContent = finalContent.slice(0, maxCharacters) + '...';
}

return [{ json: { content: finalContent, postId: $input.first().json.id } }];

// Consolidar Datos ‚Äî versi√≥n robusta

// Utilidades seguras de acceso
const safe = (fn, def = '') => {
  try { 
    const v = fn();
    return (v === undefined || v === null) ? def : v;
  } catch { 
    return def; 
  }
};

// IDs y t√≠tulo del post
const postId = safe(() => $('Conversi√≥n HTML').first().json.postId)
  || safe(() => $('Obtener Detalles del Post').first().json.id);

const postTitle = safe(() => $('Obtener Detalles del Post').first().json.title.rendered);

// Textos generados
const FacebookPost = safe(() => $('Noticia a Facebook Post').first().json.message.content);
const linkedInPost = safe(() => $('Noticia a LinkedIn Post').first().json.message.content);
const twitterPost  = safe(() => $('Noticia a Twitter Post').first().json.message.content);
const instagramPost  = safe(() => $('Noticia a Instagram Post').first().json.message.content);

// Im√°genes subidas a Drive (nombres ya distinguen linkedIn/twitter)
const FacebookImageUrl = safe(() => $('Google Drive').first().json.webViewLink);
const linkedInImageUrl = safe(() => $('Google Drive2').first().json.webViewLink);
const twitterImageUrl  = safe(() => $('Google Drive3').first().json.webViewLink);
const instagramImageUrl  = safe(() => $('Google Drive3').first().json.webViewLink);

// Salida √∫nica consolidada
return [{
  json: {
    postId,
    postTitle,
    FacebookPost,
    FacebookImageUrl,
    linkedInPost,
    linkedInImageUrl,
    twitterPost,
    twitterImageUrl,
    instagramPost,
    instagramImageUrl,
    fecha: new Date().toISOString()
  }
}];
